import React from 'react';
import HTMLFlipBook from 'react-pageflip';
import logo from '../images/macka-text-below.svg';
import './menu-style.css';

function Menu(props) {
    const pages = [
        // ... your existing data ...
    ];

    return (
        <div className="menu-container">
            <HTMLFlipBook width={window.innerWidth} height={window.innerHeight}>
                {pages.map((page, index) => (
                    <div key={index} className="demoPage">
                        <h1 className="page-title">{page.title}</h1>
                        {page.engItem && <h2 className="eng-item">{page.engItem}</h2>}
                        {index === 0 && (
                            <div className="logo-container">
                                <img className="logo" src={page.logo} alt="Logo" />
                            </div>
                        )}
                        {index !== 0 && (
                            <div className="items-container">
                                <ul>
                                    {page.items.map((item, itemIndex) => (
                                        <li key={itemIndex}>
                                            <span className="item-name">{item.name}</span>
                                            {item.price && <span className="item-price">{item.price}</span>}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        )}
                    </div>
                ))}
            </HTMLFlipBook>
        </div>
    );
}

export default Menu;
